<?php

namespace AppBundle\Repository;

/**
 * SmartphoneRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class SmartphoneRepository extends \Doctrine\ORM\EntityRepository
{
    public function getAllSmartphones()
    {
        $sql = "SELECT * FROM products
        WHERE type = :type";
        $params = array(
            'type' => 'smartphone',
        );

        return $this->getEntityManager()->getConnection()->executeQuery($sql, $params)->fetchAll();
    }

    public function specificationsForOne($id)
    {
        $sql = "SELECT * FROM products
        INNER JOIN smartphones on products.id = smartphones.product_id
        WHERE smartphones.product_id = :id";
        $params = array(
            'id' => $id,
        );

        return $this->getEntityManager()->getConnection()->executeQuery($sql, $params)->fetchAll()[0];
    }

    public function getAllSmartphonesNewToOld()
    {
        $sql = "SELECT * FROM products
        WHERE type = :type
        ORDER BY date_added DESC";
        $params = array(
            'type' => 'smartphone',
        );

        return $this->getEntityManager()->getConnection()->executeQuery($sql, $params)->fetchAll();
    }

    public function getAllSmartphonesOldToNew()
    {
        $sql = "SELECT * FROM products
        WHERE type = :type
        ORDER BY date_added ASC";
        $params = array(
            'type' => 'smartphone',
        );

        return $this->getEntityManager()->getConnection()->executeQuery($sql, $params)->fetchAll();
    }

    public function getAllSmartphonesHighToLow()
    {
        $sql = "SELECT * FROM products
        WHERE type = :type
        ORDER BY promotion_price DESC";
        $params = array(
            'type' => 'smartphone',
        );

        return $this->getEntityManager()->getConnection()->executeQuery($sql, $params)->fetchAll();
    }

    public function getAllSmartphonesLowToHigh()
    {
        $sql = "SELECT * FROM products
        WHERE type = :type
        ORDER BY promotion_price ASC";
        $params = array(
            'type' => 'smartphone',
        );

        return $this->getEntityManager()->getConnection()->executeQuery($sql, $params)->fetchAll();
    }

    public function getOnlyDiscount()
    {
        $sql = "SELECT * FROM products
        WHERE type = :type AND discount > 0";
        $params = array(
            'type' => 'smartphone',
        );

        return $this->getEntityManager()->getConnection()->executeQuery($sql, $params)->fetchAll();
    }
}
